import { Component, OnInit } from '@angular/core';
import { OrderService } from '../order.service'; // Ensure the path is correct
import { Order } from '../order.interface'; // Ensure the path is correct
import { NavController } from '@ionic/angular';

@Component({
  selector: 'app-orders',
  templateUrl: './orders.page.html',
  styleUrls: ['./orders.page.scss'],
})
export class OrdersPage implements OnInit {
  recentOrder: Order | null = null; 

  constructor(private orderService: OrderService, private navCtrl: NavController) { }

  ngOnInit() {
    this.loadRecentOrder();
  }

  loadRecentOrder() {
    const order = this.orderService.getRecentOrder();
    if (order) {
      this.recentOrder = order;
    } else {
      console.error('No recent order found');

    }
  }

}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
      <ion-back-button defaultHref="/"></ion-back-button>
    </ion-buttons>
    <ion-title>Recent orders</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true" class="white-background">

  <div *ngIf="recentOrder; else noOrdersTemplate">
    <h2>Recent Order</h2>
    <p>Order ID: {{ recentOrder.id }}</p>
    <p>Total Amount: ${{ recentOrder.totalAmount }}</p>
  </div>

 <!-- No Orders -->
<ng-template #noOrdersTemplate>
  <div class="no-orders-container">
    <p>No orders.</p>
  </div>
</ng-template>
//



</ion-content>
